<?use \Bitrix\Iblock\Model\Section;//функция дампаfunction dump($var, $all = false, $die = false){    if ($all == true) {        ?>        <font style="text-align: left; font-size: 14px"><pre><?print_r($var)?></pre></font><br>        <?    } else {        ?>        <font style="text-align: left; font-size: 14px"><pre><?var_dump($var)?></pre></font><br>        <?    }    if ($die) {        die;    }}//функция определяющая раздел каталога "Солнечные электростанции" по свойству разделаfunction getSES($iblockId, $sectionId){    $entity = Section::compileEntityByIblock($iblockId);    $filter = ['ID' => $sectionId, 'IBLOCK_ID' => $iblockId];    $section = $entity::getList([        'select' => ['UF_SES_SECTION'],        'filter' => $filter    ]);    return $section->fetchRaw()['UF_SES_SECTION'];}//функция возвращающая id раздела каталога из адреса текущей страницыfunction getCatalogSectionId($curPage){    $pageStr = trim($curPage, '/');    $arPage = explode('/', $pageStr);    if ($arPage[0] == 'catalog' && count($arPage) == 2) {        return (int) $arPage[1];    }    return 0;}